	namespace src\model; 
	use lib\getz;	
	use src\model;

	class Esqueci_a_minha_senhaInput extends getz\Inject {
			
		private $email;
			
		private $error;	
			
		public function __construct($request) { 
			if (isset($request[EMAIL])) {
				$this->email = $this->inject($request[EMAIL]);	
			}
		}
					
		public function getEmail() {
			return $this->email;
		}
		
		public function setEmail($email) {
			$this->email = $email;
		}
					
		public function getError() {
			return $this->error;
		}
		
		private function setError($error) {
			$this->error = $error;
		}	

		public function isValid($method) {		
			if ($method == POST) {
				if ($this->email == null || $this->email == STRING_EMPTY) {
					$this->setError(THE_ATTRIBUTE . EMAIL . IS_REQUIRED);
					return false;					
				} 
				else {
					return true;
				}
			}
		}
	}
	
?>